namespace Fluxera.Repository.Query{	using System.Linq;	using JetBrains.Annotations;	/// <summary>	///     A contract for a paging and/or sorting criteria builder.	/// </summary>	/// <typeparam name="T">The type of the aggregate root.</typeparam>	[PublicAPI]	public interface IQueryOptions<T> where T : class	{		/// <summary>		///     Applies the query options to the given <see cref="IQueryable{T}" />.		/// </summary>		/// <param name="queryable"></param>		/// <returns></returns>		IQueryable<T> ApplyTo(IQueryable<T> queryable);		/// <summary>		///     Tries to get the <see cref="IPagingOptions{T}" />. Returns <c>false</c>		///     if there are no paging options configured.		/// </summary>		/// <param name="pagingOptions">Outputs the paging options if available.</param>		/// <returns></returns>		bool TryGetPagingOptions(out IPagingOptions<T> pagingOptions);		/// <summary>		///     Tries to get the <see cref="ISkipTakeOptions{T}" />. Returns <c>false</c>		///     if there are no paging options configured.		/// </summary>		/// <param name="skipTakeOptions">Outputs the skip/take options if available.</param>		/// <returns></returns>		bool TryGetSkipTakeOptions(out ISkipTakeOptions<T> skipTakeOptions);		/// <summary>		///     Tries to get the <see cref="ISortingOptions{T}" />. Returns <c>false</c>		///     if there are no paging options configured.		/// </summary>		/// <param name="sortingOptions">Outputs the sorting options if available.</param>		/// <returns></returns>		bool TryGetSortingOptions(out ISortingOptions<T> sortingOptions);	}}