namespace Fluxera.Repository.Storage.InMemory{	using System.Linq;	using Fluxera.Entity;	using Fluxera.Repository.Query;	internal static class InMemoryRepositoryExtensions	{		internal static IQueryable<TAggregateRoot> ApplyOptions<TAggregateRoot>(			this IQueryable<TAggregateRoot> queryable, IQueryOptions<TAggregateRoot> queryOptions)			where TAggregateRoot : AggregateRoot<TAggregateRoot>		{			if(queryOptions.HasPagingOptions)			{				queryOptions.TryGetPagingOptions(out IPagingOptions<TAggregateRoot>? pagingOptions);				queryable = queryable.Skip(pagingOptions.Skip).Take(pagingOptions.PageSize);			}			if(queryOptions.HasSkipTakeOptions)			{				queryOptions.TryGetSkipTakeOptions(out ISkipTakeOptions<TAggregateRoot>? skipTakeOptions);				if(skipTakeOptions.Skip.HasValue)				{					queryable = queryable.Skip(skipTakeOptions.Skip.Value);				}				if(skipTakeOptions.Take.HasValue)				{					queryable = queryable.Take(skipTakeOptions.Take.Value);				}			}			if(queryOptions.HasOrderByOptions)			{				queryOptions.TryGetOrderByOptions(out IOrderByOptions<TAggregateRoot>? orderByOptions);				IOrderByExpression<TAggregateRoot> orderBy = orderByOptions.OrderByExpression;				IOrderedQueryable<TAggregateRoot> orderedQueryable = orderBy.IsDescending					? queryable.OrderByDescending(orderBy.SortExpression)					: queryable.OrderBy(orderBy.SortExpression);				if(orderByOptions.ThenByExpressions != null)				{					foreach(IOrderByExpression<TAggregateRoot> thenBy in orderByOptions.ThenByExpressions)					{						orderedQueryable = thenBy.IsDescending							? orderedQueryable.ThenBy(thenBy.SortExpression)							: orderedQueryable.ThenByDescending(thenBy.SortExpression);					}				}				queryable = orderedQueryable;			}			return queryable;		}	}}